import{_ as e,c as i,o as t,d as l}from"./app.227ba59d.js";const v='{"title":"Briif","description":"","frontmatter":{},"headers":[],"relativePath":"cvi/brief.md","lastUpdated":1654633808480}',s={},a=l('<h1 id="briif" tabindex="-1">Briif <a class="header-anchor" href="#briif" aria-hidden="true">#</a></h1><p><strong>Minu logo nii-\xF6elda kliendiks on MM20 grupp</strong></p><blockquote><p><strong>Kes/mis te olete? Nimi? Milline h\xFC\xFCdlause teid k\xF5ige paremini iseloomustaks (m\xF5elge v\xE4lja)?</strong></p><ul><li>Multimeedikud, \xF5pilased, MM20, programmeerijad, veebikujundajad, disainerid. \u201EEks, ma teen seda kodus\u201C</li></ul></blockquote><blockquote><p><strong>Teie eesm\xE4rgid: N\xE4iteks individuaalsed eesm\xE4rgid \u2013 saada haridus, leida s\xF5pru, olla koos (kellegagi endi seast v\xE4hemalt)</strong></p><ul><li>Omanda erialasid teadmisi, luua karj\xE4\xE4ri, teenida head palka, l\xF5petada Tallina Pol\xFCtehnikum</li></ul></blockquote><blockquote><p><strong>Loodetud tulemused ja visioon: Kuidas te soovite, et teid v\xF5taks need, kes teiega kokku puutuvad? (\xF5petajad, teised r\xFChmad)</strong></p><ul><li>Targalt, loovalt, virgalt, usaldusv\xE4\xE4rselt, positiivselt</li></ul></blockquote><blockquote><p><strong>Sihtturg (kohandatud m\xF5ttes) Kellele tahate muljet avaldada?</strong></p><ul><li>Iseendale, s\xF5pradele, perele, \xF5petajatele, tuttavatale</li></ul></blockquote><blockquote><p><strong>Konkurents: Kes on teie peamised konkurendid (nii otseselt kui kaudselt)?</strong></p><ul><li>Teised r\xFChmad, teised koolid</li></ul></blockquote><blockquote><p><strong>H\xE4\xE4l: Mida te soovite, et teie identiteet (ka logo \xFCksi) \xFCtleks teie kohta? Kuidas te sooviksite, et inimesed reageeriks, n\xE4hes teie identiteeti v\xF5i logo? Milline peaks olema (on) teie br\xE4ndi isik/iseloom (n\xE4iteks kui ta oleks loom, siis kes?)?</strong></p><ul><li>Loovus, kohanemisv\xF5imeline, vastupidavus. Kui meie isik oleks loom siis ta oleks panda.</li></ul></blockquote><blockquote><p><strong>Eelistused v\xF5i eeskuju: Nimetage m\xF5nd logo, milline teile meeldib ja selgitage miks. Nimetage midagi, mis enamikele inimestele meeldib, kuid mitte teile ja seda just disaini seisukohast.</strong></p><ul><li>Twitteri logo \u2013 n\xE4eb lihtne v\xE4lja, kerge silmale, sobib Twitteri isiksusega kokku, visuaalselt ligit\xF5mbav</li><li>TopShop \u2013 ebahuvitav, liiga lihtne, ei t\xF5mba ligi, logo ei edasta firma s\xF5numit</li></ul></blockquote><blockquote><p><strong>Kui teie grupp oleks loom, siis kes?</strong></p><ul><li>Panda, sest nad on v\xE4sinud, tegutsevad \xF6\xF6siti, vastupidavad.</li></ul></blockquote><blockquote><p><strong>Tulevik: Millisena n\xE4ete oma gruppi 2 aasta p\xE4rast?</strong></p><ul><li>Edukad, arenemise teel, produktiivsed, ettev\xF5tlikud, kohusetundlikud, unikaalsed</li></ul></blockquote><blockquote><p><strong>M\xE4rks\xF5nad: Aga siia m\xE4rkige ise mingid m\xE4rks\xF5nad, mis tunduvad, et on seotud teiega v\xF5i iseloomustavad teid.</strong></p><ul><li>V\xE4sinud, usaldusv\xE4\xE4rne, loov</li></ul></blockquote>',12),o=[a];function u(d,n,r,k,p,m){return t(),i("div",null,o)}var c=e(s,[["render",u]]);export{v as __pageData,c as default};
